    var p = node.parentNode, j = 0;
    if (!p) return 0;
    if (!fromList)
        fromList = p.childNodes;
    
    if (apf.hasIndexOfNodeList)
        return fromList.indexOf(node);
        
    for (var i = 0, l = fromList.length; i < l; i++) {
        if (fromList[i] == node)
            return j;
        j++;
    }
    return j;
