/**
 *
 */
apf.queue = {
    //@todo apf3.0
    q : {},
    
    timer : null,
    add : function(id, f){
        this.q[id] = f;
        if (!this.timer)
            this.timer = $setTimeout("apf.queue.empty()");
    },
    
    remove : function(id){
        delete this.q[id];
    },
    
    empty : function(prop){
        clearTimeout(this.timer);
        this.timer = null;
        
        
        if (apf.layout && apf.layout.$hasQueue)
            apf.layout.processQueue();
        
        
        if (apf.xmldb && apf.xmldb.$hasQueue)
            apf.xmldb.notifyQueued();
        
        
        var q = this.q;
        for (var prop in q){
            var f = q[prop];
            if (f) {
                delete q[prop];
                f();
            }
        }
    }
};



